sys_graphql_resolver.script:
(function process(/*ResolverEnvironment*/ env) {

    const {input} = env.getArguments();
    const {action, fieldValues} = input;
    const utils = new x_387592_from_re_0.R2UXUtils();

    if(action === "create") {

        const demandGR = utils.createDemand(fieldValues);

        return {
            sys_id: demandGR.sys_id.toString(),
            title: demandGR.getDisplayValue("title"),
            description: demandGR.getDisplayValue("description")
        };
    }

    if(action === "update") {

            const {sys_id} = input;

            const demandGR = utils.updateDemand(sys_id, fieldValues);

            return {
                sys_id: demandGR.sys_id.toString(),
                title: demandGR.getDisplayValue("title"),
                description: demandGR.getDisplayValue("description"),
                number: demandGR.getDisplayValue("number"),
                archived: demandGR.getDisplayValue("archived"),
            };
    }

})(env);
