sys_graphql_resolver.script:
(function process(/*ResolverEnvironment*/ env) {

    const {input} = env.getArguments();
    const {action} = input;
    const utils = new x_387592_from_re_0.R2UXUtils();

    if(action === "create") {

        const demandGR = utils.createDemand(input);

        return {
            sys_id: demandGR.sys_id.toString(),
            title: demandGR.getDisplayValue("title"),
            description: demandGR.getDisplayValue("description")
        };
    }

    if(action === "update") {

            const {sys_id} = input;
            delete input.action;
            delete input.sys_id;

            const demandGR = utils.updateDemand(sys_id, input);

            return {
                sys_id: demandGR.sys_id.toString(),
                title: input.title,
                description: input.description
            };
    }
    gs.info("action: " + action);

    if(action === "get") {
        const {sys_id} = input;
        const demandGR = utils.getDemand(sys_id);

        return {
            sys_id: demandGR.sys_id.toString(),
            title: demandGR.getDisplayValue("title"),
            description: demandGR.getDisplayValue("description")
        };
    }


})(env);
